$(function(){

  getDate();
  $(startTime);
  $(table);
  $(poll);

});

function dateToString(date) {
	var monthNames = [ 'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December' ];
    var month = date.getMonth() ;
    var day = date.getDate();
    var dateOfString = (('' + month).length < 2 ? '' : '') +  monthNames[month] + ' ';
    dateOfString += (('' + day).length < 2 ? '0' : '') + day + ' ';
    dateOfString += date.getFullYear();
    return dateOfString;


}
function getDate(){
	var currentdate = new Date();
	var datetime= '';
	datetime += dateToString(currentdate );


	var $header = $('<h1/>');
	 $header.append(datetime);
	$('#currentDate').replaceWith($header);
}
function startTime() {
    var today=new Date();
    var h=today.getHours();
    var m=today.getMinutes();
    var s=today.getSeconds();
    var dn='AM';
	if (h>12){
		dn='PM';
		h=h-12;
	}
    m = checkTime(m);
    s = checkTime(s);
    document.getElementById('txt').innerHTML = 'Current Time: ' +h+':'+m+':'+s+ ' '+ dn;
    setTimeout(function(){startTime();},500);
}

function checkTime(i) {
    if (i<10) {i = '0' + i;}  // add zero in front of numbers < 10
    return i;
}

function table() {
    var cols,$btn;

    $.get('/api/employee/'+'54ecaa24fb4974129dc2050d'+'/appointments/today', function( data ){
        var count = 0;
        //empty's the table
        $('#tblBody').empty();
        //for loop to reload the table
        for(var i=0; i<data.length; i++){
            var $img = $('<img id="Image" src="http://placehold.it/50x50" />');
            count++;
            var appDate = new Date(data[i].date);
            //parsing to get time
            var fhours = appDate.getHours();
            var appTime;
            if(fhours/12 < 1){
              var hours = ('0'+appDate.getHours()).slice(-2); //returns 0-
              var minutes = ('0'+appDate.getMinutes()).slice(-2); //returns 0-59
              appTime = hours+':'+minutes + ' AM';
             }
             else{
              var pmHours = appDate.getHours()%12;

              if(pmHours === 0) {
                 pmHours = 12;
              }

              var hoursPM = ('0'+pmHours).slice(-2); //returns 0-
              var minutesPM = ('0'+appDate.getMinutes()).slice(-2); //returns 0-59
              appTime = hoursPM+':'+minutesPM + ' PM';
            }
            if (data[i].state === 'checkedIn' || data[i].state === 'roomed' || data[i].state === 'done') {

                var url = '/viewform/' + data[i]._id;
                var $form = $('<a href="'+url+'" onclick="window.open(\''+url+'\', \'newwindow\', \'width=600, height=400\'); return false;" >View Forms</a>');

                if (data[i].state === 'checkedIn'){
                    var $check = $('<input type="checkbox">').data('appid',data[i]._id);
                    $check.change(function(){
                        var $appid = $(this).data('appid');

                        $.ajax({
                            url :  '/api/appointments/'+$appid+'/state',
                            type : 'PUT'
                        });
                    });

                     cols = [count,data[i].fname + ' ' + data[i].lname,$form,appTime,data[i].state,$check,$img];
                }

                else if(data[i].state === 'roomed') {
                    $btn = $('<button class="btn btn-primary"><span class="glyphicon glyphicon-ok-sign" aria-hidden="true"></span></button>');
                    var x = data[i]._id;
                     $btn.click(function() {
                        $.ajax({
                            url: '/api/appointments/'+x+'/state',
                            type: 'PUT',
                         });
                    });

                     cols = [count,data[i].fname + ' ' + data[i].lname,$form,appTime,data[i].state,$btn,$img];

                }
                else{
                    cols = [count,data[i].fname + ' ' + data[i].lname,$form,appTime,data[i].state,$btn = false,$img];
                }
            }

            else {
                cols = [count,data[i].fname + ' ' + data[i].lname,$btn = false,appTime,data[i].state,$btn = false,$img];
            }

            insRow(cols);
        }//end of for loop

    });//end of $get()
}//end of table()


function poll() {
    setTimeout(function(){
        table();

        poll();
    },1000);//checks every 1000 millisecond
}

// JQuery Insert Row
function insRow(cols) {
  var $row = $('<tr/>'); // Create a r

  // Loop through data
  for (var i = 0; i < cols.length; i++) {
    var $col = $('<td/>'); // Create a column
    $col.append(cols[i]); // Append column data to column
    $row.append($col); // Append column to row
  }

  $('#tblBody').append($row); // Append to top of element using prepend
}

// Script to add, remove, preview and submit form elements

// Insert options into appropriate dropdown field
function insertOption(dropcounter) {
    var x = document.getElementById('drop' + dropcounter);
    var option = document.createElement('option');
    option.text = prompt ('Name your option');

    //checks for null
    if (option.text === 'null'){
      return;
    }

    //checks for input
    if (option.text)
    {
       x.add(option);
    }

}

// Remove option from appropriate dropdown field
function removeOption(dropcounter) {
    var x = document.getElementById('drop' + dropcounter);
    x.remove(x.selectedIndex);
}

$(document).ready(function () {

    function preview() {
        var dropCounter = 0;
        $('#yourform').remove();

        var fieldSet = $('<fieldset id=\"yourform\"><legend>Your Form</legend></fieldset>');

        // Iterate through each field and append its HTML to the preview form
        $('#buildyourform div').each(function () {
            var id = 'input' + $(this).attr('id').replace('field', '');
            var label = $('<label for=\"' + id + '\">' + $(this).find('input.fieldname').first().val() + '</label>');
            var input;

            switch ($(this).find('select.fieldtype').first().val()) {
                case 'textbox':
                    input = $('<div class=\"previewForm\"><input type=\"text\" id=\"' + id + '\" name=\"' + id + '\" /></div>');
                    break;
                case 'dropdown':
                    dropCounter ++;
                    input = $('<div class=\"previewForm\"><select id=\"drop' + dropCounter + '\">  </select> <button type=\"button\" onclick=\"insertOption(' + dropCounter +')\">Insert option</button> <button type=\"button\" onclick=\"removeOption(' + dropCounter + ')\">Remove option</button></div>');
                    break;
            }
            fieldSet.append(label);
            fieldSet.append(input);
        });
        $('body').append(fieldSet);
    }

    // Add form creation buttons
    $('#add').click(function () {
        var intId = $('#buildyourform div').length + 1;
        var fieldWrapper = $('<div class=\"fieldwrapper\" id=\"field' + intId + '\"/>');
        var fName = $('<input type=\"text\" class=\"fieldname\" />');
        var fType = $('<select class=\"fieldtype\"><option value=\"textbox\">Text</option><option value=\"dropdown\">Drop</option> </select>');

        var removeButton = $('<input type=\"button\" class=\"remove\" value=Remove>');
        removeButton.click(function () {
            $(this).parent().remove();
        });
        fieldWrapper.append(fName);
        fieldWrapper.append(fType);
        fieldWrapper.append(removeButton);
        $('#buildyourform').append(fieldWrapper);

        fType.change(function() {
            preview();
        });

        fName.on('input', function() {
            preview();
        });

        preview();
    });

    // Create JSON object and post to database
    $('#submit').click(function() {
        var json = {
            business: 'temp',
            fields: [
            ]
        };
        var dropCounter = 0;
        var form = $('fieldset');

        // Cycle through each preview form field
        form.children().each(function(){

            // Check field type and insert appropriate fields
            if ($(this).find('select.fieldtype').first().val()) {
                if ($(this).find('select.fieldtype').first().val() === 'dropdown') {
                    var fieldname = $(this).find('input.fieldname').first().val();
                    var dropJson = {
                        type: 'dropdown',
                        label: fieldname,
                        options: []
                    };

                    dropCounter ++;
                    var options = $('#drop' + dropCounter + ' option');

                    $.map(options ,function(option) {
                        dropJson.options.push(option.value);
                    });

                    json.fields.push(dropJson);
                }
                else {
                    json.fields.push({type: 'textfield', label: $(this).find('input.fieldname').first().val()});
                }
            }
        });

        $.ajax({
            url:'/api/m/form',
            type:'POST',
            data: JSON.stringify(json),
            contentType:'application/json',
            dataType:'json',
            success:function(){}
        });
    });
});

